$(document).ready(function() {
  console.log("Hello from Signup Validation");
  $("#new_user").validate({
    rules: {
      "user[firstname]": {
        required: true,
        maxlength: 25
      },
      "user[lastname]": {
        required: true,
        maxlength: 25
      },
      "user[password]": {
        required: true,
        minlength: 6,
        maxlength: 255
      },
      "user[password_confirmation]": {
        required: true,
        minlength: 6,

        equalTo: "#user_password"
      }
    },
    messages: {
      "user[firstname]": "Please enter your firstname",
      "user[lastname]": "Please enter your lastname",
      "user[password]": {
        required: "Please enter your password",
        minlength: "Your password must has at least 6 characters"
      },
      "user[password_confirmation]": {
        required: "Please enter your password",
        minlength: "Your password must has at least 6 characters",
        equalTo: "Please enter the same password as above"
      }
    },
  });
})
